from pybricks.parameters import Stop

from robot import COLOR_SENSOR, DRIVE_BASE, HUB, LEFT_ATTACHMENT, RIGHT_ATTACHMENT

def run():
    drive_straight_distance_(250)

def heading():
    return HUB.imu.heading()

def drive_straight_distance_(distance):
    DRIVE_BASE.straight(distance, then=Stop.COAST)

def turn_angle_(angle):
    DRIVE_BASE.turn(angle, then=Stop.COAST)

def turn_left_attachment__speed__angle_(speed, angle):
    LEFT_ATTACHMENT.run_angle(speed, angle, Stop.COAST)

def turn_right_attachment__speed__angle__2(speed, angle):
    RIGHT_ATTACHMENT.run_angle(speed, angle, Stop.COAST)

def left_attachment_to_end_stop_speed__power_limit_(speed, power_limit):
    LEFT_ATTACHMENT.run_until_stalled(speed, Stop.COAST, power_limit)

def left_attachment_to_end_stop_speed__power_limit__2(speed, power_limit):
    LEFT_ATTACHMENT.run_until_stalled(speed, Stop.COAST, power_limit)


